package day16_forloop;

public class C03_forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		//10 ile 30 arasındaki sayıları araları virgüllü yazdır
		
		for (int i = 10; i <= 30; i++) {
			if (i==30) {
			System.out.println(i);
		} else {
             System.out.println(i+",");
		}
		
			 
	            
	      
		}
	
	
	}

}
